    majorsMinors(
        name: "Avatud Ühiskonna Tehnoloogiad",
        level: .masters,
        type: .digitehnoloogia,
        requirements: [
            Requirements(term: "Bakalaureuse kraad"),
            Requirements(term: "Inglise keel oskus vähemalt B2 tasemel"),
